                    quote_stock_symbol: 'AMD',
                    source_group_id: '25a35a2d-6755-408a-92cb-9d3791e9839c',
                    source_url: 'https://finance.yahoo.com/news/forget-nvidia-2-super-semiconductor-094000044.html',
                    polymorphic_source: null,
                    author: 'Anthony Di Pizio, The Motley Fool',
                    last_updated_date: '2024-05-23T09:40:00+00:00',
